    <div className="recruitDetailsContainer">
      {/* <img src={hero.image} />
       <h2>{hero.name}</h2>
      <p>{hero.description}</p>  */}
      <button onClick={handleBack}> Go Back</button>
      <button onClick={()=>{deselectClickHandler()}}> Deselect </button>

    </div>
  );
}

export default TeamDetails;
