//Have to run the 2 command below to make the page show poperly on your browser 
//npm install @material-ui/core 
//npm install @material-ui/icons

import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';



const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

function SignIn({ onExistingTeamLogIn }) {
  const classes = useStyles();

  const [enteredTeam, setEnteredTeam] = useState("")

  const history = useHistory()

  const verifyTeam = (users, addedTeam) => {
    let allUsers = users.map(team => team.teamName)
    let currentUser = addedTeam.teamName
    if (allUsers.includes(currentUser)) {
      onExistingTeamLogIn(currentUser)
      history.push("/")
    } else {
      window.alert("The team does not exist")
    }
  } 

  const newTeamSubmit = (e) => {
    e.preventDefault();
    const addedTeam = {
      teamName: enteredTeam
    }
    fetch('http://localhost:3000/users')
    .then(response => response.json())
    .then(users => verifyTeam(users, addedTeam))  
    // onNewTeamSubmit(addedTeam)
    // history.push("/")
  }

  const handleTeamName = (e) => {
    setEnteredTeam(e.target.value)
  }

  return (
    <Container component="main" maxWidth="xs" className="signInPage">
      <h1>this is the sign In page</h1>
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form onSubmit={newTeamSubmit} className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Enter your existing team name"
            name="email"
            autoComplete="email"
            autoFocus
            onChange={handleTeamName}
            value={enteredTeam}
          />
          {/* <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          /> */}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="/signup" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
    </Container>
  );
}

export default SignIn;
