    for (let i =0 ; i <= heroSelectionArray.length; i++) {
      i < heroSelectionArray.length ? 
        fetch("http://localhost:3000/teamMember", {
          method: 'POST',
          headers: { 
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            userId: currentTeam,
            heroId: heroSelectionArray[i].id,
            name: heroSelectionArray[i].name,
            image: `${heroSelectionArray[i].thumbnail.path}.${heroSelectionArray[i].thumbnail.extension}`,
            description:heroSelectionArray[i].description
            })
        }) : history.push("/team")
    }

    setHeroSelectionArray([]);

  }


    return (


        <div className="flex-container">
          <h1>{currentTeam}</h1>
          <br></br>
            <div className={classes.root}>
              <Grid container spacing={3}>
                {heroSelectionArray.map(hero => <HeroCard 
                                                    key={hero.id} 
                                                    hero={hero} 
                                                    heroImage={hero.thumbnail.path + "." + hero.thumbnail.extension}
                                                    heroId={hero.id}
                                                    // link={`/recruit/${hero.id}`}
                                                    linke={""}
                                                    hoverEffectDisabledId={"hoverEffectDisabledId"}
                                                    xsNum={xsNum}
                                                    smNum={smNum}
                                                    seletedMemberCard={seletedMemberCard}
                                                    onDisselectBtnClick={onDisselectBtnClickInSelection}
                                                    />)}
              </Grid>
            </div>    
            <button className="add-to-team-btn" onClick={()=>{handleAddToTeamBtn()}}>Add To Your Team</button>
        </div>
  );
}

export default HeroSelection;
